  * file and version.
  *************************************************************************
  * $Id: M_InOut_Cancel.sql,v 1.2 2002/08/13 05:27:36 jjanke Exp $
  ***
  * Title:   Cancel Delivery of Order by reversing it
  * Description:
  ************************************************************************/
  -- Logistice
  v_ResultStr VARCHAR2(2000):='';
  v_Message VARCHAR2(2000):='';
  v_Record_ID VARCHAR2(32);
  -- Parameter
  TYPE RECORD IS REF CURSOR;
    Cur_Parameter RECORD;
    -- Parameter Variables
    --
    Cur_InOut RECORD;
    v_AD_Client_ID VARCHAR2(32);

  BEGIN
    --  Update AD_PInstance
    IF(p_PInstance_ID IS NOT NULL) THEN
      DBMS_OUTPUT.PUT_LINE('Updating PInstance - Processing ' || p_PInstance_ID) ;
      v_ResultStr:='PInstanceNotFound';
      AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'Y', NULL, NULL) ;
      -- Get Parameters
      v_ResultStr:='ReadingParameters';
      FOR Cur_Parameter IN
        (SELECT i.Record_ID,
          p.ParameterName,
          p.P_String,
          p.P_Number,
          p.P_Date
        FROM AD_PInstance i
        LEFT JOIN AD_PInstance_Para p
          ON i.AD_PInstance_ID=p.AD_PInstance_ID
        WHERE i.AD_PInstance_ID=p_PInstance_ID
        ORDER BY p.SeqNo
        )
      LOOP
        v_Record_ID:=Cur_Parameter.Record_ID;
      END LOOP; -- Get Parameter
      DBMS_OUTPUT.PUT_LINE('  Record_ID=' || v_Record_ID) ;
    ELSE
      v_Record_ID:=p_Order_ID;
    END IF;
  BEGIN --BODY
    -- For all Deliveries of Order
    FOR Cur_InOut IN
      (SELECT *
      FROM M_InOut
      WHERE C_Order_ID=v_Record_ID
        AND DocStatus<>'RE' -- Not the existing reversal trx
        FOR UPDATE
      )
    LOOP
      DBMS_OUTPUT.PUT_LINE('Cancel Shipment ' || Cur_InOut.DocumentNo || ', ID=' || Cur_InOut.M_InOut_ID) ;
      -- Cancel Order
      UPDATE M_InOut
        SET DocAction='RC', -- Reverse Correction
        Processed='N',
        Updated=now()
      WHERE M_InOut_ID=Cur_InOut.M_InOut_ID;
      M_INOUT_POST(NULL, Cur_InOut.M_InOut_ID) ;
    END LOOP;
    --<<FINISH_PROCESS>>
    IF(p_PInstance_ID IS NOT NULL) THEN
      --  Update AD_PInstance
      DBMS_OUTPUT.PUT_LINE('Updating PInstance - Finished ' || v_Message) ;
      AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'N', 1, v_Message) ;
    END IF;
    RETURN;
  END; --BODY
EXCEPTION
WHEN OTHERS THEN
  v_ResultStr:= '@ERROR=' || SQLERRM;
  DBMS_OUTPUT.PUT_LINE(v_ResultStr) ;
  IF(p_PInstance_ID IS NOT NULL) THEN
    ROLLBACK;
    AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'N', 0, v_ResultStr) ;
  ELSE
    RAISE;
  END IF;
  RETURN;
END M_INOUT_CANCEL
