  v_Aux NUMBER;

BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;

    SELECT COUNT(*)
      INTO v_Aux
    FROM AD_WINDOW T
   WHERE T.AD_MODULE_ID = :new.AD_MODULE_ID;

     IF v_Aux > 0 AND :new.istranslationrequired='N' THEN
       RAISE_APPLICATION_ERROR(-20103, 'The module has User Interface artifacts, translation required must be set to Yes (checked).') ;
     END IF;

    SELECT COUNT(*)
      INTO v_Aux
    FROM AD_TAB T
   WHERE T.AD_MODULE_ID = :new.AD_MODULE_ID;

     IF v_Aux > 0 AND :new.istranslationrequired='N' THEN
       RAISE_APPLICATION_ERROR(-20103, 'The module has User Interface artifacts, translation required must be set to Yes (checked).') ;
     END IF;

    SELECT COUNT(*)
      INTO v_Aux
    FROM AD_FIELD T
   WHERE T.AD_MODULE_ID = :new.AD_MODULE_ID;

     IF v_Aux > 0 AND :new.istranslationrequired='N' THEN
       RAISE_APPLICATION_ERROR(-20103, 'The module has User Interface artifacts, translation required must be set to Yes (checked).') ;
     END IF;

    SELECT COUNT(*)
      INTO v_Aux
    FROM AD_ELEMENT T
   WHERE T.AD_MODULE_ID = :new.AD_MODULE_ID;

     IF v_Aux > 0 AND :new.istranslationrequired='N' THEN
       RAISE_APPLICATION_ERROR(-20103, 'The module has User Interface artifacts, translation required must be set to Yes (checked).') ;
     END IF;

    SELECT COUNT(*)
      INTO v_Aux
    FROM AD_TEXTINTERFACES T
   WHERE T.AD_MODULE_ID = :new.AD_MODULE_ID;

     IF v_Aux > 0 AND :new.istranslationrequired='N' THEN
       RAISE_APPLICATION_ERROR(-20103, 'The module has User Interface artifacts, translation required must be set to Yes (checked).') ;
     END IF;

    SELECT COUNT(*)
      INTO v_Aux
    FROM AD_MESSAGE T
   WHERE T.AD_MODULE_ID = :new.AD_MODULE_ID;

     IF v_Aux > 0 AND :new.istranslationrequired='N' THEN
       RAISE_APPLICATION_ERROR(-20103, 'The module has User Interface artifacts, translation required must be set to Yes (checked).') ;
     END IF;

    SELECT COUNT(*)
      INTO v_Aux
    FROM AD_FORM T
   WHERE T.AD_MODULE_ID = :new.AD_MODULE_ID;

     IF v_Aux > 0 AND :new.istranslationrequired='N' THEN
       RAISE_APPLICATION_ERROR(-20103, 'The module has User Interface artifacts, translation required must be set to Yes (checked).') ;
     END IF;

    SELECT COUNT(*)
      INTO v_Aux
    FROM AD_MENU T
   WHERE T.AD_MODULE_ID = :new.AD_MODULE_ID;

     IF v_Aux > 0 AND :new.istranslationrequired='N' THEN
       RAISE_APPLICATION_ERROR(-20103, 'The module has User Interface artifacts, translation required must be set to Yes (checked).') ;
     END IF;

    SELECT COUNT(*)
      INTO v_Aux
    FROM AD_WORKFLOW T
   WHERE T.AD_MODULE_ID = :new.AD_MODULE_ID;

     IF v_Aux > 0 AND :new.istranslationrequired='N' THEN
       RAISE_APPLICATION_ERROR(-20103, 'The module has User Interface artifacts, translation required must be set to Yes (checked).') ;
     END IF;

END AD_MODULE_TRANSLATION_TRG
