exist NUMBER:= 0; indTempName NVARCHAR2(50) := 'System Customization'; indTempPackage NVARCHAR2(50) := 'mySystem.CustomizationTemplate';
BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
If :old.customization_Allowed='N' and :new.customization_Allowed='Y' then
select count(*) into exist from AD_MODULE where TYPE = 'T' and ISINDEVELOPMENT = 'Y';
if (exist > 0) then RAISE_APPLICATION_ERROR(-20631,'There is already an active Industry Template.'); else Insert into AD_MODULE ( AD_MODULE_ID,AD_CLIENT_ID,AD_ORG_ID,ISACTIVE,CREATED,CREATEDBY,UPDATED,UPDATEDBY, NAME,VERSION,DESCRIPTION, HELP, URL,TYPE,LICENSE, ISINDEVELOPMENT,ISDEFAULT,SEQNO,JAVAPACKAGE, LICENSETYPE,AUTHOR,STATUS,UPDATE_AVAILABLE,ISTRANSLATIONREQUIRED,AD_LANGUAGE,HASCHARTOFACCOUNTS, ISTRANSLATIONMODULE,HASREFERENCEDATA,ISREGISTERED,UPDATEINFO,UPDATE_VER_ID) values ( get_uuid(),'0','0','Y',now(),'0',now(),'0', to_char(indTempName),'1.0.0', to_char(indTempName) || ' is an autogenerated industry template to store all the customizations made to module core.', to_char(indTempName) || ' is an autogenerated industry template to store all the customizations made to module core. Only one industry template can be active at the same time.', null,'T','The Openbravo Public License Version 1.1 ("OBPL") consists of the Mozilla Public License Version 1.1, modified to be specific to Openbravo, with the Additional Terms in Exhibit B. The original Mozilla Public License 1.1 can be found at: http://www.mozilla.org/MPL/MPL-1.1.html', 'Y','N',null,to_char(indTempPackage), 'OtherOS',null,null,null,'Y','en_US','N', 'N','N',null,null,null); end if; end if;
If (:old.customization_Allowed='Y' and :new.customization_Allowed='N') then delete from AD_MODULE where name = indTempName; end if;
END AD_SYSTEM_INFO_TRG2
