trigger.body:
/************************************************************************* * The contents of this file are subject to the Openbravo Public License * Version 1.0 (the "License"), being the Mozilla Public License * Version 1.1 with a permitted attribution clause; you may not use this * file except in compliance with the License. You may obtain a copy of * the License at http://www.openbravo.com/legal/license.html * Software distributed under the License is distributed on an "AS IS" * basis, WITHOUT WARRANTY OF ANY KIND, either express or implied. See the * License for the specific language governing rights and limitations * under the License. * The Original Code is Openbravo ERP. * The Initial Developer of the Original Code is Openbravo SL * All portions are Copyright (C) 2008 Openbravo SL * All Rights Reserved. * Contributor(s): ______________________________________. ************************************************************************/ devTemplate NUMBER; devModule CHAR(1); cuerrentID VARCHAR2(32); cuerrentModuleID VARCHAR2(32);
BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
SELECT COUNT(*) INTO devTemplate FROM AD_MODULE WHERE IsInDevelopment = 'Y' AND Type = 'T';
IF (UPDATING OR INSERTING) THEN cuerrentID := :new.AD_Table_ID; cuerrentModuleID := :new.AD_Package_ID; ELSE cuerrentID := :old.AD_Table_ID; cuerrentModuleID := :old.AD_Package_ID; END IF;
SELECT M.IsInDevelopment INTO devModule FROM AD_MODULE M, AD_PACKAGE P WHERE M.AD_MODULE_ID = P.AD_MODULE_ID AND P.AD_PACKAGE_ID = cuerrentModuleID;
IF (UPDATING AND devTemplate=0 AND devModule='N') THEN IF ( COALESCE(:NEW.AD_Client_ID , '.') != COALESCE(:OLD.AD_Client_ID , '.') OR COALESCE(:NEW.AD_Org_ID , '.') != COALESCE(:OLD.AD_Org_ID , '.') OR COALESCE(:NEW.IsActive , '.') != COALESCE(:OLD.IsActive , '.') OR COALESCE(:NEW.Name , '.') != COALESCE(:OLD.Name , '.') OR COALESCE(:NEW.Description , '.') != COALESCE(:OLD.Description , '.') OR COALESCE(:NEW.Help , '.') != COALESCE(:OLD.Help , '.') OR COALESCE(:NEW.TableName , '.') != COALESCE(:OLD.TableName , '.') OR COALESCE(:NEW.IsView , '.') != COALESCE(:OLD.IsView , '.') OR COALESCE(:NEW.AccessLevel , '.') != COALESCE(:OLD.AccessLevel , '.') OR COALESCE(:NEW.AD_Window_ID , '.') != COALESCE(:OLD.AD_Window_ID , '.') OR COALESCE(:NEW.IsSecurityEnabled , '.') != COALESCE(:OLD.IsSecurityEnabled , '.') OR COALESCE(:NEW.IsDeleteable , '.') != COALESCE(:OLD.IsDeleteable , '.') OR COALESCE(:NEW.IsHighVolume , '.') != COALESCE(:OLD.IsHighVolume , '.') OR COALESCE(:NEW.ImportTable , '.') != COALESCE(:OLD.ImportTable , '.') OR COALESCE(:NEW.IsChangeLog , '.') != COALESCE(:OLD.IsChangeLog , '.') OR COALESCE(:NEW.PO_Window_ID , '.') != COALESCE(:OLD.PO_Window_ID , '.') OR COALESCE(:NEW.IsDefaultAcct , '.') != COALESCE(:OLD.IsDefaultAcct , '.') OR COALESCE(:NEW.Developmentstatus , '.') != COALESCE(:OLD.Developmentstatus , '.') OR COALESCE(:NEW.AD_Package_ID , '.') != COALESCE(:OLD.AD_Package_ID , '.')
--SQL_Record_Identifier is not going to be exported ) THEN RAISE_APPLICATION_ERROR(-20532, 'Cannot update an object in a module not in developement and without an active template'); END IF; END IF;
IF ((DELETING OR INSERTING) AND devModule='N') THEN RAISE_APPLICATION_ERROR(-20533, 'Cannot insert/delete objects in a module not in development.'); END IF; END AD_TABLE_MOD_TRG
