BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF INSERTING THEN -- Create Translation Row INSERT INTO AD_Task_Trl ( AD_Task_ID, AD_Language, AD_Client_ID, AD_Org_ID, IsActive, Created, CreatedBy, Updated, UpdatedBy, Name, Description, IsTranslated ) SELECT :new.AD_Task_ID, AD_Language, :new.AD_Client_ID, :new.AD_Org_ID, :new.IsActive, :new.Created, :new.CreatedBy, :new.Updated, :new.UpdatedBy, :new.Name, :new.Description, 'N' FROM AD_Language WHERE IsActive='Y' AND IsSystemLanguage='Y'; END IF; -- Inserting IF UPDATING THEN IF(COALESCE(:old.Name, '.') <> COALESCE(:NEW.Name, '.') OR COALESCE(:old.Description, '.') <> COALESCE(:NEW.Description, '.')) THEN -- Translation UPDATE AD_Task_Trl SET IsTranslated='N', Updated=now() WHERE AD_Task_ID=:new.AD_Task_ID; END IF; END IF; -- Updating END AD_TASK_TRG
