trigger.body:
/************************************************************************* * The contents of this file are subject to the Openbravo Public License * Version 1.0 (the "License"), being the Mozilla Public License * Version 1.1 with a permitted attribution clause; you may not use this * file except in compliance with the License. You may obtain a copy of * the License at http://www.openbravo.com/legal/license.html * Software distributed under the License is distributed on an "AS IS" * basis, WITHOUT WARRANTY OF ANY KIND, either express or implied. See the * License for the specific language governing rights and limitations * under the License. * The Original Code is Openbravo ERP. * The Initial Developer of the Original Code is Openbravo SLU * All portions are Copyright (C) 2008 Openbravo SLU * All Rights Reserved. * Contributor(s): ______________________________________. ************************************************************************/ devTemplate NUMBER; devModule CHAR(1); cuerrentID VARCHAR2(32); cuerrentModuleID VARCHAR2(32);
BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
SELECT COUNT(*) INTO devTemplate FROM AD_MODULE WHERE IsInDevelopment = 'Y' AND Type = 'T';
IF (UPDATING OR INSERTING) THEN cuerrentID := :new.AD_WF_Node_ID; cuerrentModuleID := :new.AD_WorkFlow_ID; ELSE cuerrentID := :old.AD_WF_Node_ID; cuerrentModuleID := :old.AD_WorkFlow_ID; END IF;
SELECT M.IsInDevelopment INTO devModule FROM AD_MODULE M, AD_WORKFLOW T WHERE M.AD_MODULE_ID = T.AD_MODULE_ID AND T.AD_WORKFLOW_ID = cuerrentModuleID;
IF (UPDATING AND devTemplate=0 AND devModule='N') THEN IF ( COALESCE(:NEW.AD_Client_ID , '.') != COALESCE(:OLD.AD_Client_ID , '.') OR COALESCE(:NEW.AD_Org_ID , '.') != COALESCE(:OLD.AD_Org_ID , '.') OR COALESCE(:NEW.IsActive , '.') != COALESCE(:OLD.IsActive , '.') OR COALESCE(:NEW.Name , '.') != COALESCE(:OLD.Name , '.') OR COALESCE(:NEW.Description , '.') != COALESCE(:OLD.Description , '.') OR COALESCE(:NEW.Help , '.') != COALESCE(:OLD.Help , '.') OR COALESCE(:NEW.AD_Workflow_ID , '.') != COALESCE(:OLD.AD_Workflow_ID , '.') OR COALESCE(:NEW.IsCentrallyMaintained , '.') != COALESCE(:OLD.IsCentrallyMaintained , '.') OR COALESCE(:NEW.Action , '.') != COALESCE(:OLD.Action , '.') OR COALESCE(:NEW.AD_Window_ID , '.') != COALESCE(:OLD.AD_Window_ID , '.') OR COALESCE(:NEW.Workflow_ID , '.') != COALESCE(:OLD.Workflow_ID , '.') OR COALESCE(:NEW.AD_Task_ID , '.') != COALESCE(:OLD.AD_Task_ID , '.') OR COALESCE(:NEW.AD_Process_ID , '.') != COALESCE(:OLD.AD_Process_ID , '.') OR COALESCE(:NEW.AD_Form_ID , '.') != COALESCE(:OLD.AD_Form_ID , '.') OR COALESCE(:NEW.Xposition , 0) != COALESCE(:OLD.Xposition , 0) OR COALESCE(:NEW.Yposition , 0) != COALESCE(:OLD.Yposition , 0) OR 1=2) THEN RAISE_APPLICATION_ERROR(-20532, 'Cannot update an object in a module not in developement and without an active template'); END IF; END IF;
IF ((DELETING OR INSERTING) AND devModule='N') THEN RAISE_APPLICATION_ERROR(-20533, 'Cannot insert/delete objects in a module not in development.'); END IF; END AD_WF_NODE_MOD_TRG
