trigger.body:
/************************************************************************* * The contents of this file are subject to the Openbravo Public License * Version 1.0 (the "License"), being the Mozilla Public License * Version 1.1 with a permitted attribution clause; you may not use this * file except in compliance with the License. You may obtain a copy of * the License at http://www.openbravo.com/legal/license.html * Software distributed under the License is distributed on an "AS IS" * basis, WITHOUT WARRANTY OF ANY KIND, either express or implied. See the * License for the specific language governing rights and limitations * under the License. * The Original Code is Openbravo ERP. * The Initial Developer of the Original Code is Openbravo SL * All portions are Copyright (C) 2001-2009 Openbravo SL * All Rights Reserved. * Contributor(s): ______________________________________. ************************************************************************/ TYPE RECORD IS REF CURSOR; CUR_DATA RECORD; v_JavaPackage VARCHAR2(260); v_ModuleMapping VARCHAR2(260);
BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF((COALESCE(:old.Name, '.') <> COALESCE(:NEW.Name, '.') OR COALESCE(:old.IsActive, '.') <> COALESCE(:NEW.IsActive, '.') OR COALESCE(:old.AD_Module_ID,'.') <> COALESCE(:new.AD_Module_ID,'.'))) THEN SELECT JavaPackage INTO v_JavaPackage FROM AD_MODULE M WHERE AD_MODULE_ID = :New.AD_Module_ID;
IF v_JavaPackage IS NOT NULL THEN v_ModuleMapping := '/'||v_JavaPackage; v_JavaPackage := v_JavaPackage||'.'||AD_MAPPING_FORMAT(TO_CHAR(:NEW.NAME)); ELSE v_ModuleMapping := '/'; v_JavaPackage := AD_MAPPING_FORMAT(TO_CHAR(:NEW.NAME)); END IF;
FOR CUR_DATA IN ( SELECT MO.AD_MODEL_OBJECT_ID, AD_MAPPING_FORMAT(TO_CHAR(:NEW.NAME)) AS WINDOWNAME, AD_MAPPING_FORMAT(TO_CHAR(T.NAME)) AS TABNAME FROM AD_MODEL_OBJECT MO, AD_TAB T WHERE MO.AD_TAB_ID=T.AD_TAB_ID AND MO.ACTION='W' AND T.AD_WINDOW_ID=:NEW.AD_WINDOW_ID ) LOOP UPDATE AD_MODEL_OBJECT SET CLASSNAME=('org.openbravo.erpWindows.' || v_JavaPackage || '.' || CUR_DATA.TABNAME), ISACTIVE=:NEW.ISACTIVE WHERE AD_MODEL_OBJECT_ID=CUR_DATA.AD_MODEL_OBJECT_ID and classname not like '%ComboReload%'; UPDATE AD_MODEL_OBJECT_MAPPING SET MAPPINGNAME=(v_ModuleMapping || CUR_DATA.WINDOWNAME || '/' || CUR_DATA.TABNAME || '_Relation.html'), ISACTIVE=:NEW.ISACTIVE WHERE AD_MODEL_OBJECT_ID=CUR_DATA.AD_MODEL_OBJECT_ID AND MAPPINGNAME LIKE '%_Relation.html'; UPDATE AD_MODEL_OBJECT_MAPPING SET MAPPINGNAME=(v_ModuleMapping || CUR_DATA.WINDOWNAME || '/' || CUR_DATA.TABNAME || '_Edition.html'), ISACTIVE=:NEW.ISACTIVE WHERE AD_MODEL_OBJECT_ID=CUR_DATA.AD_MODEL_OBJECT_ID AND MAPPINGNAME LIKE '%_Edition.html'; UPDATE AD_MODEL_OBJECT_MAPPING SET MAPPINGNAME=(v_ModuleMapping || CUR_DATA.WINDOWNAME || '/' || CUR_DATA.TABNAME || '_Excel.xls'), ISACTIVE=:NEW.ISACTIVE WHERE AD_MODEL_OBJECT_ID=CUR_DATA.AD_MODEL_OBJECT_ID AND MAPPINGNAME LIKE '%_Excel.xls'; END LOOP; END IF; END AD_WINDOW_TRG2
