  v_Count NUMBER;


BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;


  --  Default Accounts for all AcctSchema
  IF(UPDATING) THEN
    IF (:NEW.C_COUNTRY_ID <> :OLD.C_COUNTRY_ID OR :NEW.C_COUNTRY_ID IS NULL) THEN
      SELECT COUNT(IBAN)
      INTO v_Count
      FROM C_BANKACCOUNT
      WHERE C_BANK_ID=:NEW.C_BANK_ID;
      IF (v_Count <> 0) THEN
        RAISE_APPLICATION_ERROR(-20259, 'An IBAN account is defined for this bank. Country must not be changed.') ;
      END IF;
    END IF;
    IF ((:NEW.CODEBANK IS NULL) OR (:NEW.CODEBRANCH IS NULL) OR (:NEW.DIGITCONTROL IS NULL)) THEN
      SELECT COUNT(CODEACCOUNT)
      INTO v_Count
      FROM C_BANKACCOUNT
      WHERE C_BANK_ID=:NEW.C_BANK_ID;
      IF (v_Count <> 0) THEN
        RAISE_APPLICATION_ERROR(-20265, 'An Spanish account is defined for this bank. Bank Code, Branch Code and Control Digit must be defined') ;
      END IF;
    END IF;
  END IF;
EXCEPTION
 WHEN NO_DATA_FOUND THEN
  RAISE_APPLICATION_ERROR(-20259, 'An IBAN account is defined for this bank. Country must not be changed.') ;
END C_BANKCOUNTRY_TRG
