IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF(INSERTING OR UPDATING) THEN SELECT C.STDPRECISION INTO V_PRECISION FROM C_INVOICE I, C_CURRENCY C WHERE I.C_CURRENCY_ID=C.C_CURRENCY_ID AND I.C_INVOICE_ID=:NEW.C_INVOICE_ID;
IF (ROUND(TO_NUMBER(:NEW.QTYINVOICED) * TO_NUMBER(:NEW.PRICEACTUAL),V_PRECISION)!=TO_NUMBER(:NEW.LINENETAMT)) THEN RAISE_APPLICATION_ERROR(-20501,'Line Amount Is Not Correct'); END IF; END IF; END C_INVOICELINE_TRG4
