BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
SELECT COUNT(*) INTO v_Count FROM C_PROJECTLINE WHERE C_Project_ID=:NEW.C_Project_ID; IF(v_Count > 0) THEN -- Update Summary Info SELECT SUM(PlannedAmt), SUM(PlannedQty), SUM(PlannedMarginAmt), SUM(CommittedAmt), SUM(CommittedQty), SUM(InvoicedAmt), SUM(InvoicedQty), (SUM(COALESCE(PlannedPrice, 0) *PlannedQty)) INTO :NEW.PlannedAmt, :NEW.PlannedQty, :NEW.PlannedMarginAmt, :NEW.CommittedAmt, :NEW.CommittedQty, :NEW.InvoicedAmt, :NEW.InvoicedQty, :NEW.PlannedPOAmt FROM C_PROJECTLINE WHERE C_Project_ID=:NEW.C_Project_ID; END IF; /* SELECT MAX(UPDATED) INTO :NEW.LASTPLANNEDPROPOSALDATE FROM C_PROJECTPROPOSALTASK T WHERE EXISTS (SELECT 1 FROM C_PROJECTPROPOSAL P WHERE P.C_PROJECTPROPOSAL_ID=T.C_PROJECTPROPOSAL_ID AND P.C_PROJECT_ID=:NEW.C_PROJECT_ID);*/ END C_PROJECT_TRG2
