BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
-- Is RevenueRecognition Time Based SELECT IsTimeBased INTO v_IsTimeBased FROM C_RevenueRecognition WHERE C_RevenueRecognition_ID=:new.C_RevenueRecognition_ID; -- Add Service Plan IF(v_IsTimeBased='Y') THEN -- Get InvoiveQty SELECT QtyInvoiced, M_Product_ID INTO v_Qty, v_M_Product_ID FROM C_InvoiceLine WHERE C_InvoiceLine_ID=:new.C_InvoiceLine_ID; -- Insert AD_Sequence_Next('C_ServiceLevel', :new.AD_Client_ID, v_NextNo) ; INSERT INTO C_ServiceLevel ( C_ServiceLevel_ID, C_RevenueRecognition_Plan_ID, AD_Client_ID, AD_Org_ID, IsActive, Created, CreatedBy, Updated, UpdatedBy, M_Product_ID, Description, ServiceLevelInvoiced, ServiceLevelProvided, Processing, Processed ) VALUES ( v_NextNo, :new.C_RevenueRecognition_Plan_ID, :new.AD_Client_ID, :new.AD_Org_ID, 'Y', now(), :new.CreatedBy, now(), :new.UpdatedBy, v_M_Product_ID, NULL, v_Qty, 0, 'N', 'N' ) ; END IF; END C_REVENUERECOGNITION_PLAN_TRG
