BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
v_docType_ID:=:NEW.C_DocType_ID; SELECT( CASE isSOTrx WHEN 'N' THEN 'V+' ELSE 'C-' END ) INTO v_movementType FROM C_DOCTYPE WHERE C_DocType_ID=v_docType_ID; :NEW.MOVEMENTTYPE:=v_movementType; END M_INOUT_TRG_PROV
