BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF; IF INSERTING THEN INSERT INTO OBKMO_Widget_Class_Trl (OBKMO_WIDGET_CLASS_TRL_ID , OBKMO_WIDGET_CLASS_ID, AD_LANGUAGE, AD_CLIENT_ID , AD_ORG_ID , ISACTIVE , CREATED , CREATEDBY , UPDATED , UPDATEDBY , TITLE , DESCRIPTION, ISTRANSLATED) SELECT get_uuid() , :new.OBKMO_WIDGET_CLASS_ID, l.AD_LANGUAGE, :new.AD_CLIENT_ID , :new.AD_ORG_ID , :new.ISACTIVE, now() , :new.CreatedBy , now() , :new.UpdatedBy , :new.TITLE , :new.DESCRIPTION, 'N' FROM AD_Language l, AD_Module m WHERE l.isActive = 'Y' AND l.IsSystemLanguage='Y' AND m.AD_Module_ID = :new.AD_Module_ID AND M.AD_Language != l.AD_Language; END IF; IF UPDATING THEN IF coalesce(:new.title,'.') != coalesce(:old.title,'.') or coalesce(:new.description, '.') != coalesce(:old.description, '.') THEN UPDATE OBKMO_Widget_Class_Trl SET IsTranslated='N', Updated=now() WHERE OBKMO_Widget_Class_ID=:new.OBKMO_Widget_Class_ID; END IF; END IF;
END OBKMO_WIDGETCLASS_TRG
