  * the legal folder of your Openbravo installation.
  * Software distributed under the License is distributed on an
  * "AS IS" basis, WITHOUT WARRANTY OF ANY KIND, either express or
  * implied. See the License for the specific language governing rights
  * and limitations under the License.
  * The Original Code is  Compiere  ERP &  Business Solution
  * The Initial Developer of the Original Code is Jorg Janke and ComPiere, Inc.
  * Portions created by Jorg Janke are Copyright (C) 1999-2001 Jorg Janke,
  * parts created by ComPiere are Copyright (C) ComPiere, Inc.;
  * All Rights Reserved.
  * Contributor(s): Openbravo SLU
  * Contributions are Copyright (C) 2001-2008 Openbravo, S.L.U.
  *
  * Specifically, this derivative work is based upon the following Compiere
  * file and version.
  *************************************************************************
  * $Id: R_Request_ReOpen.sql,v 1.2 2003/01/12 04:17:33 jjanke Exp $
  ***
  * Title: Reopen Requests
  * Description:
  ************************************************************************/
  -- Logistice
  v_ResultStr VARCHAR2(2000):='';
  v_Message VARCHAR2(2000):='';
  v_Result NUMBER:=1; -- 0=failure
  v_Record_ID VARCHAR2(32);
  v_AD_User_ID VARCHAR2(32);
  -- Parameter
  TYPE RECORD IS REF CURSOR;
    Cur_Parameter RECORD;
    -- Parameter Variables
    p_R_Request_ID VARCHAR2(32) ;
    v_count NUMBER(10):=0;
    v_R_RequestAction_ID VARCHAR2(32) ;
    rowcount NUMBER;
  BEGIN
    --  Update AD_PInstance
    DBMS_OUTPUT.PUT_LINE('Updating PInstance - Processing ' || p_PInstance_ID) ;
    v_ResultStr:='PInstanceNotFound';
    AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'Y', NULL, NULL) ;
  BEGIN --BODY
    -- Get Parameters
    v_ResultStr:='ReadingParameters';
    FOR Cur_Parameter IN
      (SELECT i.Record_ID,
        i.AD_User_ID,
        p.ParameterName,
        p.P_String,
        p.P_Number,
        p.P_Date
      FROM AD_PInstance i
      LEFT JOIN AD_PInstance_Para p
        ON i.AD_PInstance_ID=p.AD_PInstance_ID
      WHERE i.AD_PInstance_ID=p_PInstance_ID
      ORDER BY p.SeqNo
      )
    LOOP
      v_Record_ID:=Cur_Parameter.Record_ID;
      v_AD_User_ID:=Cur_Parameter.AD_User_ID;
      IF(Cur_Parameter.ParameterName='R_Request_ID') THEN
        p_R_Request_ID:=Cur_Parameter.P_String;
        DBMS_OUTPUT.PUT_LINE('  R_Request_ID=' || p_R_Request_ID) ;
      ELSE
        DBMS_OUTPUT.PUT_LINE('*** Unknown Parameter=' || Cur_Parameter.ParameterName) ;
      END IF;
    END LOOP; -- Get Parameter
    DBMS_OUTPUT.PUT_LINE('  Record_ID=' || v_Record_ID) ;
    IF(p_R_Request_ID IS NOT NULL) THEN
      v_ResultStr:='Updating';
      UPDATE R_Request
        SET Processed='N'
      WHERE R_Request_ID=p_R_Request_ID
        AND Processed='Y';
      rowcount:=SQL%ROWCOUNT;
      v_count:=rowcount;
      IF(v_count=1) THEN
        v_ResultStr:='Inserting Action';
        AD_Sequence_Next('R_RequestAction', p_R_Request_ID, v_R_RequestAction_ID) ;
        DBMS_OUTPUT.PUT_LINE('Inserting R_RequestAction_ID=' || v_R_RequestAction_ID) ;
        INSERT
        INTO R_RequestAction
          (
            R_RequestAction_ID, AD_Client_ID, AD_Org_ID, IsActive,
            CREATED, CREATEDBY, UPDATED, UPDATEDBY,
            R_Request_ID, AD_USER_ID, ACTIONTYPE, RESULT
          )
        SELECT  v_R_RequestAction_ID, AD_Client_ID, AD_Org_ID, 'Y',
          now(), v_AD_User_ID, now(), v_AD_User_ID,
          p_R_Request_ID, v_AD_User_ID, 'X', '** ReOpended **'
        FROM R_Request
        WHERE R_Request_ID=p_R_Request_ID;
      END IF;
    END IF;
    v_Message:='@Updated@=' || v_count;
    --<<FINISH_PROCESS>>
    --  Update AD_PInstance
    DBMS_OUTPUT.PUT_LINE('Updating PInstance - Finished ' || v_Message) ;
    AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'N', v_Result, v_Message) ;
    RETURN;
  END; --BODY
EXCEPTION
WHEN OTHERS THEN
   v_ResultStr:= '@ERROR=' || SQLERRM;
  DBMS_OUTPUT.PUT_LINE(v_ResultStr) ;
  ROLLBACK;
  AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'N', 0, v_ResultStr) ;
  RETURN;
END R_REQUEST_REOPEN
