  devTemplate NUMBER;
  devModule   CHAR(1);
  cuerrentID  VARCHAR2(32);
  cuerrentModuleID  VARCHAR2(32);
  vAux NUMBER;
BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;

 --Check if trying to move object from module not in dev
  IF (UPDATING) THEN
    IF (COALESCE(:NEW.AD_Module_ID , '.') != COALESCE(:OLD.AD_Module_ID , '.')) THEN
      SELECT COUNT(*) 
        INTO vAux
        FROM AD_MODULE
       WHERE AD_MODULE_ID = :old.AD_Module_ID
        AND isindevelopment = 'N';
      IF (vAux!=0) THEN
        RAISE_APPLICATION_ERROR(-20000, '@ChangeNotInDevModule@');
      END IF;
    END IF;
  END IF;

  SELECT COUNT(*)
    INTO devTemplate
    FROM AD_MODULE
   WHERE IsInDevelopment = 'Y'
     AND Type = 'T';

  IF (UPDATING OR INSERTING) THEN
    cuerrentID := :new.AD_Property_Configuration_ID;
    cuerrentModuleID := :new.AD_Module_ID;
  ELSE
    cuerrentID := :old.AD_Property_Configuration_ID;
    cuerrentModuleID := :old.AD_Module_ID;
  END IF;

  SELECT M.IsInDevelopment
    INTO devModule
    FROM AD_MODULE M
   WHERE M.AD_MODULE_ID = cuerrentModuleID;

  IF (UPDATING AND devTemplate=0 AND devModule='N') THEN
    IF (COALESCE(:NEW.AD_CLIENT_ID                , '.') != COALESCE(:NEW.AD_CLIENT_ID                , '.') OR
        COALESCE(:NEW.AD_ORG_ID                   , '.') != COALESCE(:NEW.AD_ORG_ID                   , '.') OR
        COALESCE(:NEW.ISACTIVE                    , '.') != COALESCE(:NEW.ISACTIVE                    , '.') OR
        COALESCE(:NEW.PROPERTY                    , '.') != COALESCE(:NEW.PROPERTY                    , '.') OR
        COALESCE(:NEW.AD_MODULE_ID                , '.') != COALESCE(:NEW.AD_MODULE_ID                , '.')) THEN
      RAISE_APPLICATION_ERROR(-20532, '@20532@');
    END IF;
  END IF;

  IF ((DELETING OR INSERTING) AND devModule='N') THEN
    RAISE_APPLICATION_ERROR(-20533, '@20533@');
  END IF;

  --Check the only updated column is "selected". In this case maitin updated
  --column as it was to prevent changes detection when trying to update database
  IF (UPDATING) THEN
    IF (COALESCE(:NEW.AD_CLIENT_ID                , '.') = COALESCE(:NEW.AD_CLIENT_ID                , '.') AND
        COALESCE(:NEW.AD_ORG_ID                   , '.') = COALESCE(:NEW.AD_ORG_ID                   , '.') AND
        COALESCE(:NEW.ISACTIVE                    , '.') = COALESCE(:NEW.ISACTIVE                    , '.') AND
        COALESCE(:NEW.PROPERTY                    , '.') = COALESCE(:NEW.PROPERTY                    , '.') AND
        COALESCE(:NEW.VALUE                       , '.') = COALESCE(:NEW.VALUE                       , '.') AND
        COALESCE(:NEW.AD_MODULE_ID                , '.') = COALESCE(:NEW.AD_MODULE_ID                , '.') AND
      :NEW.SELECTED != :OLD.SELECTED) THEN
         :NEW.UPDATED := :OLD.UPDATED;
    END IF;
  END IF;
END AD_PROPERTY_CONF_MOD_TRG
