V_Count NUMBER:= 0; TYPE RECORD IS REF CURSOR; Cur_Period RECORD; Begin
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
For Cur_Period In (Select * From C_Period Where C_Period_Id In (Select Distinct(Cp.C_Period_Id) From C_Period Cp Join C_Periodcontrol Cpc On Cpc.C_Period_Id= Cp.C_Period_Id Where Periodstatus <> 'P')) Loop Select Count(*) Into V_COUNT From C_Period Where C_Period_Id <> Cur_Period.C_Period_Id And Ad_Org_Id=Cur_Period.Ad_Org_Id And Ad_Client_Id=Cur_Period.Ad_Client_Id And Cur_Period.startdate <= Enddate And Cur_Period.Enddate >= Startdate; If (V_COUNT > 0) Then RAISE_APPLICATION_ERROR(-20000, '@DatesOverlapped@'); End if; End Loop;
END C_PERIOD_TRG3
